const input = $input.first().json.body || $input.first().json;

const date = input['Date'] || new Date().toLocaleDateString('fr-FR');
const nom = input['Nom'] || 'Client';
const prenom = input['Prénom'] || '';
const email = input['Email'] || '';
const prixHT = input['Prix HT'] || 0;
const tva = input['TVA'] || 20;
const prixTTC = input['Prix TTC'] || (prixHT * (1 + tva/100));
const numFacture = input['Numéro de facture'] || 'F-' + Date.now();
const numDevis = input['Numéro de devis'] || 'N/A';
const description = input['Description'] || 'Travaux de rénovation';

const html = `
<!DOCTYPE html>
<html>
<head><meta charset="utf-8"><style>
  body { font-family: 'Segoe UI', Arial, sans-serif; margin: 40px; color: #333; }
  .header { display: flex; justify-content: space-between; border-bottom: 3px solid #16a34a; padding-bottom: 20px; margin-bottom: 30px; }
  .company { font-size: 24px; font-weight: bold; color: #16a34a; }
  .facture-num { font-size: 14px; color: #666; }
  .client-info { background: #f0fdf4; padding: 20px; border-radius: 8px; margin-bottom: 30px; }
  table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
  th { background: #16a34a; color: white; padding: 12px; text-align: left; }
  td { padding: 12px; border-bottom: 1px solid #e2e8f0; }
  .total-row { font-weight: bold; font-size: 18px; color: #16a34a; }
  .payment { background: #fefce8; padding: 15px; border-radius: 8px; border: 1px solid #fde047; margin-top: 20px; }
  .footer { margin-top: 40px; padding-top: 20px; border-top: 1px solid #e2e8f0; font-size: 12px; color: #666; }
</style></head>
<body>
  <div class="header">
    <div class="company">AMG Rénovation</div>
    <div><div class="facture-num">Facture ${numFacture}</div><div>Date: ${date}</div><div>Réf. Devis: ${numDevis}</div></div>
  </div>
  <div class="client-info">
    <strong>Client:</strong> ${prenom} ${nom}<br>
    <strong>Email:</strong> ${email}
  </div>
  <table>
    <thead><tr><th>Description</th><th>Montant HT</th></tr></thead>
    <tbody><tr><td>${description}</td><td>${prixHT.toLocaleString('fr-FR')} €</td></tr></tbody>
  </table>
  <table>
    <tr><td>Total HT</td><td><strong>${prixHT.toLocaleString('fr-FR')} €</strong></td></tr>
    <tr><td>TVA (${tva}%)</td><td>${(prixTTC - prixHT).toLocaleString('fr-FR')} €</td></tr>
    <tr class="total-row"><td>Total TTC</td><td>${prixTTC.toLocaleString('fr-FR')} €</td></tr>
  </table>
  <div class="payment">⚠️ <strong>Paiement attendu sous 30 jours</strong> par virement bancaire.<br>IBAN: FR76 1234 5678 9012 3456 7890 123 — BIC: AMGFRPPXXX</div>
  <div class="footer">AMG Rénovation — SIRET: 123 456 789 00012 — TVA: FR12345678901</div>
</body>
</html>`;

return [{ json: { success: true, document_type: 'facture', numero: numFacture, client: `${prenom} ${nom}`, total_ttc: prixTTC, html: html } }];
